mapper.insert:
INSERT 
  INTO MEMBER_ACCS_LOG
(
       MEMBER_NO
     , ACCESS_ADDR
     , ACCESS_PATH
)
VALUES
(
      #{memberNo}
     ,#{accessAddr}
     ,#{accessPath}
)

mapper.select:
SELECT MEMBER_NO
     , ACCESS_ADDR
     , ACCESS_DATE
     , ACCESS_PATH
  FROM MEMBER_ACCS_LOG
 WHERE TO_CHAR(ACCESS_DATE, 'YYYYMMDD') = TO_CHAR(SYSDATE, 'YYYYMMDD')
   AND ACCESS_ADDR                      = #{accessAddr}
   AND ACCESS_PATH                      = #{accessPath}
   AND MEMBER_NO                        = #{memberNo}
