mapper.select:
		SELECT CONTENTID
		  FROM ZZIM
		 WHERE CONTENTID = #{contentId}
           AND MEMBER_NO = #{memberNo}

mapper.delete:
DELETE
FROM ZZIM 
WHERE MEMBER_NO = #{memberNo}
AND CONTENTID = #{contentId}

